import React from "react"
import Header from './components/header'
import Image from './components/image'
import logo from './img/slide.png'

class App extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            text: "Help text",
            userData: "", 
        }

        this.inputClick = this.inputClick.bind(this)
    }

    render(){
        return (
            <div>
                <Header title="Шапка сайта" id="header"/>
                <h1 onClick={this.inputClick}>{this.state.text}</h1>
                <input></input>
                <p>{this.state.text === "Help text" ? "True" : "False"}</p> 
                <Image src={logo} />
                <img className="img" src={logo}></img>
            </div>
        )
    }

    inputClick() {
        this.setState({text: "Changed"})
        console.log("Click!")
    }
}

export default App
