import React from "react"
import * as ReactDOMClient from "react-dom/client"

class Header extends React.Component {
    render() {
        return(
          <header>{this.props.title} {this.props.id}</header>
        )
    }  
}

class App extends React.Component {
    text = "Help text"
    render(){
        return (
            <div>
                <Header title="Шапка сайта" id="header"/>
                <h1 onClick={this.inputClick}>{this.text}</h1>
                <p>{this.text === "Hello?" ? "True" : "False"}</p> 
            </div>
        )
    }

    inputClick() {console.log("Click!")}

}

export default App
