import React from "react"

class Form extends React.Component {

    render(){
        return (
            <div className="form">
                <form>
                    <input className="nameInput" placeholder="Введеите задание" onChange={(e) => this.setState({name: e.target.value})}></input>
                    <button className="buttonSubmit"onClick={() => {
                        this.newForm.reset()
                        this.props.onAdd({
                            name: this.state.name,
                            })
                        }}
                        >Добавить
                    </button>
                </form>
            </div>
        )
    }

}

export default Form
